import React, { useEffect, useState } from 'react';
import styles from '../pages/less/antd_m.less';
import { Menu } from 'antd';
import {useNavigate, useLocation} from 'react-router-dom';
import { ReadOutlined, EditOutlined, DatabaseOutlined } from '@ant-design/icons';
import { Button  } from 'antd';

export default function Aside() {
    const navigate = useNavigate();
    const location = useLocation();
    const [defaultKey, setDefaultKey] = useState('');

    // 一般加个空数组就是为了模仿componentDidMounted
    useEffect(()=>{
        let path = location.pathname;
        let key = path.split('/')[1];
        setDefaultKey(key);
    }, [location.pathname]);

    const handleClick = e => {
        navigate('/'+e.key);
        setDefaultKey(e.key);
    };

        return (

            <div className={styles.alert}>
                <div className={styles.lqDemo}>
                    <Button>Default Button</Button>
                    <Button>Default Button</Button>
                </div>
            <Menu
                onClick={handleClick}
                style={{ width: '220px' ,height:'700px',marginTop:'-2px',backgroundImage: `url("https://i.postimg.cc/bwM7d6dR/1.jpg")` }}
                selectedKeys={[defaultKey]}
                mode="inline"
            >
                <Menu.Item key="GoogleMap_" style={{color:"white"}}><ReadOutlined /> Search nearest store</Menu.Item>
                <Menu.Item  className={styles.alert} key="ListTable" style={{color:"whiteSmoke"}}><ReadOutlined /> 创作电影评论</Menu.Item>
                <Menu.Item key="edit" style={{color:"white"}}><EditOutlined /> 电影简介编辑</Menu.Item>
                <Menu.Item key="means" style={{color:"white"}}><DatabaseOutlined /> 热门影评广场</Menu.Item>
                <Menu.Item key="Demo" style={{color:"white"}}><DatabaseOutlined /> 电影专场</Menu.Item>
            </Menu>
            </div>
        )
}
