/* jshint esversion: 6 */

import React, {useEffect, useRef, useState} from 'react';
import {GoogleMap, useJsApiLoader} from '@react-google-maps/api';
import {FindUserDataApi} from './request/api';
import firebase from "firebase/compat";
import axios from "axios";
import {Input} from "antd";
import { Card } from 'antd';

const { Search } = Input;


const containerStyle = {
    width: '600px',
    height: '400px',
    float:'left'
};


export default function map() {

    const firebaseConfig = {
        apiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c",
        authDomain: "urban-computing-cb07c.firebaseapp.com",
        databaseURL: "https://urban-computing-cb07c-default-rtdb.firebaseio.com",
        projectId: "urban-computing-cb07c",
        storageBucket: "urban-computing-cb07c.appspot.com",
        messagingSenderId: "293478437107",
        appId: "1:293478437107:web:4cd5199b68f68eac542786",
        measurementId: "G-3Z5DD1SJYQ"
    };
    const [count, setCount] =React.useState({lat: 53.49332, lng: -6.3});

    React.useEffect(()=>{
            firebase.initializeApp(firebaseConfig);
            firebase.database().ref('/').on('value',(snap)=> {
                let obj = snap.val();
                if (obj !== null) {
                    for (var key in obj) {
                        if(key.split(',')[2]>'22 13:35:00') {
                            setCount(obj[key]);
                            console.log(obj[key]);
                        }
                    }
                }
            });
    },[]);

    const NameArray=['12321','432532','324252'];
    const {isLoaded} = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c"
    });

    const [myArr, setMyArr] = React.useState([]);
    const Wrapper = styled.section`
  padding: 4em;
  background: papayawhip;
`;
    function QueryData(value:string){
        axios.get('https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
            params: {
                key: 'AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c',
                types: value,
                location: '53.49332,-6.31718',
                radius: '1000000'
            }
        }).then(function (response) {
            const result = response.data.results;
            for (var key in result) {
                myArr.push(result[key].geometry.location);
                axios.get('/api/add', {
                    params: {
                        id:0,
                        title:result[key].geometry.location,
                        text:result[key].name
                    }
                });
            }
            window.location.reload();
            console.log(myArr);
        })
            .catch(function (error) {
                console.log(error);
            });
    }

    const forceUpdate= React.useCallback((map) =>{

            const marker1 = new window.google.maps.Marker({
                position: count,
                title: "starting"
            });
            console.log(count);
            FindUserDataApi({}).then(response => {
                const result = response;
                for (var key in result) {
                    var lat = [parseFloat(result[key].title.replace(/\"/g, "").split(',')[0].split(':')[1]), count.lat];
                    var lng = [parseFloat(result[key].title.replace(/\"/g, "").split(',')[1].split(':')[1].replace(/\}/g, "")), count.lng];
                    var R = 6378137;
                    var dLat = (lat[1] - lat[0]) * Math.PI / 180;
                    var dLng = (lng[1] - lng[0]) * Math.PI / 180;
                    var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat[0] * Math.PI / 180) * Math.cos(lat[1] * Math.PI / 180) * Math.sin(dLng / 2) * Math.sin(dLng / 2);
                    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
                    var d = R * c;
                    if ((d < 100000) && (result[key].text!=="")) {
                        const marker = new window.google.maps.Marker({
                            position: {
                                lat: parseFloat(result[key].title.replace(/\"/g, "").split(',')[0].split(':')[1]),
                                lng: parseFloat(result[key].title.replace(/\"/g, "").split(',')[1].split(':')[1].replace(/\}/g, ""))
                            },
                        });
                        NameArray.push(result[key].text);
                        marker.setMap(map);
                        console.log(NameArray);
                    }
                }
            });
            marker1.setMap(map);
        },[]);



    return isLoaded?(
        <div>
            <GoogleMap style={{Left:'10px',fontWeight:'bold',marginTop:'10px',color:'#0066FF'}}  mapContainerStyle={containerStyle}  center={count}  zoom={10}  onLoad={forceUpdate}> </GoogleMap>
            <Search style={{width:'330px',marginLeft:'100px',marginBottom:'5px',marginTop:'0px'}} placeholder="Search nearest store" onSearch={QueryData}></Search>
                {NameArray.map(item => (
                <Card
                    key={item}
                    title={item}
                    bordered={true}
                    style={{
                        width:'300px',
                        height:'200px',
                        marginLeft:'700px',
                        marginTop:'10px'
                    }}
                >
                </Card>
                ))}
        </div>):<></>

}
