import request from './request'

// 注册
export const RegisterApi = (params) => request.post('/register', params)
//登录
export const LoginApi = (params) => request.post('/login', params)
//获取文章列表
export const ArticleListApi = (params) => request.get('/posts', {params})

export const ArticleAddApi = (params) => request.post('/posts', params)

// 查看文章
export const ArticleSearchApi = (params) => request.get(`/posts/${params.id}`)

// 重新编辑文章
export const ArticleUpdateApi = (params) => request.put('/article/update', params)
