/* jshint esversion: 6 */

import React, {useState} from 'react';
import {GoogleMap, Marker, useJsApiLoader} from '@react-google-maps/api';
// Import the functions you need from the SDKs you need
import firebase from "firebase/compat";
import axios from "axios";
import {Input} from "antd";
const { Search } = Input;


const containerStyle = {
    width: '800px',
    height: '400px'
};

export default function map() {
    const firebaseConfig = {
        apiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c",
        authDomain: "urban-computing-cb07c.firebaseapp.com",
        databaseURL: "https://urban-computing-cb07c-default-rtdb.firebaseio.com",
        projectId: "urban-computing-cb07c",
        storageBucket: "urban-computing-cb07c.appspot.com",
        messagingSenderId: "293478437107",
        appId: "1:293478437107:web:4cd5199b68f68eac542786",
        measurementId: "G-3Z5DD1SJYQ"
    };
    const [count, setCount] =React.useState({lat: 53.49332, lng: -6.31718});
    let myArr=[];

    React.useEffect(()=>{
            firebase.initializeApp(firebaseConfig);
            firebase.database().ref('/').on('value',(snap)=> {
                let obj = snap.val();
                if (obj !== null) {
                    for (var key in obj) {
                        if(key.split(',')[2]>'22 13:35:00') {
                            setCount(obj[key]);
                            console.log(obj[key]);
                        }
                    }
                }
            });
    },[]);


    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c"
    });

    const [map, setMap] = React.useState(null);

    const onLoad = function callback(map) {
        // const bounds = new window.google.maps.LatLngBounds(count);
        // map.fitBounds(bounds);
        const marker1 = new window.google.maps.Marker({
            position: count,
            title: "starting"
        });
        axios.get('https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
            params: {
                key: 'AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c',
                types: 'dinner',
                location: '53.26,-6.15',
                radius: '100000'
            }
        }).then(function (response) {
            const result = response.data.results;
            for (var key in result) {
                myArr.push(result[key].geometry.location);
            }
            for (let key in myArr) {
                const marker = new window.google.maps.Marker({
                    position: myArr[key],
                });
                marker.setMap(map);
                console.log(myArr[key]);
            }
        });
        console.log(count);
        marker1.setMap(map);
    };


    const query= function(value:string){
        axios.get('https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
            params: {
                key: 'AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c',
                types: value===''?'cafe':value,
                location: '53.26,-6.15',
                radius: '100000'
            }
        }).then(function (response) {
            const result = response.data.results;
            for (var key in result) {
                myArr.push(result[key].geometry.location);
            }
            for(let key in myArr) {
                const marker = new window.google.maps.Marker({
                    position: myArr[key],
                    title: "starting"
                });
                marker.setMap(map);
                console.log(myArr[key]);
                return isLoaded ?(
                    <div>
                    <GoogleMap  mapContainerStyle={containerStyle}  center={count}  zoom={20} >
                        <Marker  position={myArr[key]}/>
                    </GoogleMap>
                    </div>
                ): <></>
            }
        })
            .catch(function (error) {
                console.log(error);
            });
    };


    return isLoaded ? (
        <div>
            <GoogleMap  mapContainerStyle={containerStyle}  center={count}  zoom={20}  onLoad={onLoad}> </GoogleMap>
            <p style={{marginLeft:'5px',fontWeight:'bold',marginTop:'10px',color:'#0066FF'}}>搜索附近餐饮店:</p><Search style={{width:'330px',marginLeft:'110px',marginBottom:'5px',marginTop:'-42px'}} placeholder="input search text" onSearch={query}   enterButton />
        </div>
    ) : <></>
}
