import React, { useEffect, useState } from 'react'
import { PageHeader, Button, Modal, Form, Input, message } from 'antd';
import moment from 'moment'
import E from 'wangeditor'
import {AddTextApi, AddUserDataApi, ArticleSearchApi, ArticleUpdateApi} from './request/api'
import { useParams, useNavigate, useLocation } from 'react-router-dom'

let editor = null

export default function Edit() {
    const [text, setText] = useState('')
    const location = useLocation()
    const [title, setTitle] = useState('')
    const [isModalVisible, setIsModalVisible] = useState(false);
    const navigate = useNavigate()
    const [form] = Form.useForm();
    const params = useParams()
    useEffect(() => {
        setTitle(title);
    }, [title])
    useEffect(() => {
        setText(text);
    }, [text])
    // 处理请求数据
    const dealData = (errCode, msg) => {
        setIsModalVisible(false); // 关闭对话框
        if (errCode === 0) {
            message.success(msg)
            setTimeout(() => {
                // 跳回list页面
                navigate('/lists')
            }, 1500)
        } else {
            message.error(msg)
        }
    }
    const handleCancel = () => {
        setIsModalVisible(false);
    };
    // 对话框点击了提交
   function handleOk () {
        let params = {
            id: '2',
            title: '没有bug',
            text: text,
        }
        console.log(params)
        if (params.id != '' && params.title != '' && params.text != '') {
            AddTextApi({params}).then(function (response) {
                console.log(response);
                window.location.reload()
            })
                .catch(function (error) {
                    console.log(error);
                });
        }
       setIsModalVisible(false);
    };

    // 模拟componentDidMount
    useEffect(() => {
        editor = new E('#div1')
        editor.config.onchange = (newHtml) => {
            setText(newHtml)
        }
        editor.create()

        // 根据地址栏id做请求
        if (params.id) {
            ArticleSearchApi({ id: '6'}).then(res => {
                if (res.errCode === 0) {
                    editor.txt.html(res.data.comment) // 重新设置编辑器内容
                    setTitle(res.data.title)
                    setText(res.data.text)
                }
            })
        }


        return () => {
            // 组件销毁时销毁编辑器  注：class写法需要在componentWillUnmount中调用
            editor.destroy()
        }
    }, [location.pathname])

    return (
        <div>
            <PageHeader
                ghost={false}
                onBack={params.id ? () => window.history.back() : null}
                title="文章编辑"
                subTitle={"当前日期：" + moment(new Date()).format("YYYY-MM-DD")}
                extra={<Button key="1" type="primary" onClick={() => setIsModalVisible(true)}>提交文章</Button>}
            >
            </PageHeader>

            <div id="div1" style={{ padding: '0 20px 20px', background: '#fff' }}></div>
            <Modal zIndex={99999} title="填写文章标题" visible={isModalVisible} onOk={handleOk.bind(this,title,text)}  onCancel={handleCancel}>
                <Form
                    form={form}
                    name="basic"
                    labelCol={{ span: 3 }}
                    wrapperCol={{ span: 21 }}
                    autoComplete="off"
                    initialValues={{title,text }}
                >
                    <Form.Item
                        label="标题"
                        name="title"
                        value={title}
                        rules={[{ required: true, message: '请填写标题' }]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        label="副标题"
                        name="text"
                        value={text}
                    >
                        <Input />
                    </Form.Item>
                </Form>
            </Modal>
        </div>
    )
}
