import { DislikeFilled, DislikeOutlined, LikeFilled, LikeOutlined } from '@ant-design/icons';
import React, {createElement, useEffect, useState} from 'react';
import {Avatar, Comment, Tooltip, Button, Form, Input, List, Skeleton, Divider} from 'antd';
import moment from 'moment';
import { message } from 'antd';
import VirtualList from 'rc-virtual-list';
import {AddCommentDataApi, CommentListApi, GetCommentDataApi} from "./request/api";
import InfiniteScroll from 'react-infinite-scroll-component';
import TextArea from "antd/es/input/TextArea";


export default function Means() {
    const [action, setAction] = useState(null);
    const [likes, setLikes] = useState(0);
    const [dislikes, setDislikes] = useState(0);
    const like = () => {
        setLikes(1);
        setDislikes(0);
        setAction('liked');
    };
    console.log(likes)
    const dislike = () => {
        setLikes(0);
        setDislikes(1);
        setAction('disliked');
    };
    const[arr,setArr]=useState([
        {
            actions:[
                <Tooltip key="comment-basic-like" title="Like">
                <span onClick={like}>
               {createElement(action === 'liked' ? LikeFilled : LikeOutlined)}
               <span className="comment-action">{likes}</span>
                </span>
                </Tooltip>,
                <Tooltip key="comment-basic-dislike" title="Dislike">
                <span onClick={dislike}>
                {React.createElement(action === 'disliked' ? DislikeFilled : DislikeOutlined)}
                <span className="comment-action">{dislikes}</span>
                </span>
                </Tooltip>,
                <span key="comment-basic-reply-to">Reply to</span>,
            ],
            author: 'Han Solo',
            avatar: 'https://joeschmoe.io/api/v1/random',
            comments: (
                <p>
                    We supply a series of design principles, practical patterns and high quality design
                    resources (Sketch and Axure), to help people create their product prototypes beautifully and
                    efficiently.
                </p>
            ),
            datetime: (
                <Tooltip title={moment().subtract(1, 'days').format('YYYY-MM-DD HH:mm:ss')}>
                    <span>{moment().subtract(1, 'days').fromNow()}</span>
                </Tooltip>
            ),
        },
        {
            actions: [
                <Tooltip key="comment-basic-like" title="Like">
                <span onClick={like}>
                {createElement(action === 'liked' ? LikeFilled : LikeOutlined)}
                <span className="comment-action">{likes}</span>
                </span>
                </Tooltip>,
                <Tooltip key="comment-basic-dislike" title="Dislike">
                <span onClick={dislike}>
                {React.createElement(action === 'disliked' ? DislikeFilled : DislikeOutlined)}
                <span className="comment-action">{dislikes}</span>
                </span>
                </Tooltip>,
                <span key="comment-list-reply-to-0">Reply to</span>
            ],
            author: 'Han Solo',
            avatar: 'https://joeschmoe.io/api/v1/random',
            comments: (
                <p>
                    We supply a series of design principles, practical patterns and high quality design
                    resources (Sketch and Axure), to help people create their product prototypes beautifully and
                    efficiently.
                </p>
            ),
            datetime: (
                <Tooltip title={moment().subtract(2, 'days').format('YYYY-MM-DD HH:mm:ss')}>
                    <span>{moment().subtract(2, 'days').fromNow()}</span>
                </Tooltip>
            ),
        },
    ]);


    const CommentList = ({ comments }) => (
        <List
            dataSource={comments}
            header={`${comments.length} ${comments.length > 1 ? 'replies' : 'reply'}`}
            itemLayout="horizontal"
            renderItem={(props) => <Comment {...props} />}
        />
    );

    const Editor = ({ onChange, onSubmit, submitting, value }) => (
        <>
            <Form.Item>
                <TextArea rows={4} onChange={onChange} value={value} />
            </Form.Item>
            <Form.Item>
                <Button htmlType="submit" loading={submitting} onClick={onSubmit} type="primary">
                    Add Comment
                </Button>
            </Form.Item>
        </>
    );

    const [comments, setComments] = useState([]);
    const [author, setAuthor] = useState([]);
    const [submitting, setSubmitting] = useState(false);
    const [value, setValue] = useState('');

    const handleSubmit = () => {
       let params={
           id:'8',
           author:author,
           comments:comments,
           value:value
       }
        AddCommentDataApi({params}).then(response=>{
            console.log(response);
            window.location.reload()
        })
            .catch(function (error) {
            console.log(error);
        })
    };

    const handleChange = (e) => {
        setValue(e.target.value);
    };

    useEffect(()=>{
        GetCommentDataApi().then(response=> {
            let newArr = JSON.parse(JSON.stringify(response));//深拷贝
            let myarr = []//用来获取自己想要的属性
            newArr.map(item => {
                let obj = {
                    id: item.id,
                    avatar: 'https://joeschmoe.io/api/v1/random',
                    actions: [
                        <Tooltip key="comment-basic-like" title="Like">
                        <span onClick={like}>
                        {createElement(action === 'liked' ? LikeFilled : LikeOutlined)}
                        <span className="comment-action">{item.likes}</span>
                        </span>
                        </Tooltip>,
                        <Tooltip key="comment-basic-dislike" title="Dislike">
                        <span onClick={dislike}>
                        {React.createElement(action === 'disliked' ? DislikeFilled : DislikeOutlined)}
                        <span className="comment-action">{dislikes}</span>
                        </span>
                        </Tooltip>,
                        <span key="comment-list-reply-to-0">Reply to</span>
                    ],
                    author: item.author,
                    comments: item.comments,
                    value: item.value,
                    datetime: (
                        <Tooltip title={moment().subtract(2, 'days').format('YYYY-MM-DD HH:mm:ss')}>
                            <span>{moment().subtract(2, 'days').fromNow()}</span>
                        </Tooltip>
                    ),
                }
                myarr.push(obj);
            })
            setArr(myarr)
            console.log(myarr)
        })
    },[])

   return(
       <div  id="scrollableDiv"
             style={{
                 height: 600,
                 overflow: 'auto',
                 padding: '0 16px',
                 border: '1px solid rgba(140, 140, 140, 0.35)',
             }}
       >
           <InfiniteScroll
               dataLength={arr.length}
               hasMore={arr.length < 50}
               endMessage={<Divider plain>It is all, nothing more 🤐</Divider>}
               scrollableTarget="scrollableDiv"
           >
       <List
           className="comment-list"
           header={`${arr.length} replies`}
           itemLayout="horizontal"
           dataSource={arr}
           renderItem={(item) => (
               <li>
                   <Comment
                       actions={item.actions}
                       author={item.author}
                       avatar={item.avatar}
                       content={item.value}
                       datetime={item.datetime}
                   />
               </li>
           )}
       />
       </InfiniteScroll>

           {comments.length > 0 && <CommentList comments={comments} />}
           <Comment
               avatar={<Avatar src="https://joeschmoe.io/api/v1/random" alt="Han Solo" />}
               content={
                   <Editor
                       onChange={handleChange}
                       onSubmit={handleSubmit}
                       submitting={submitting}
                       value={value}
                   />
               }
           />
       </div>
    );
}
