/* jshint esversion: 6 */

import React, {useState} from 'react';
import { GoogleMap, useJsApiLoader } from '@react-google-maps/api';
// Import the functions you need from the SDKs you need
import firebase from "firebase/compat";
import axios from "axios";
import {Input} from "antd";
const { Search } = Input;


const containerStyle = {
    width: '800px',
    height: '400px'
};

const getApi = () => {
    axios.get('https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
        params: {
            key:'AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c',
            types:'restaurant',
            location:'53.26,-6.15',
            radius:'100000'
        }
    }).then(function (response) {
        const result = response.data.results;
        for(var key in result){
        console.log(result[key].geometry.location);
        }
    })
        .catch(function (error) {
            console.log(error);
        });
};
getApi();

export default function map() {
    const [array,setArray]=useState([]);
    const firebaseConfig = {
        apiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c",
        authDomain: "urban-computing-cb07c.firebaseapp.com",
        databaseURL: "https://urban-computing-cb07c-default-rtdb.firebaseio.com",
        projectId: "urban-computing-cb07c",
        storageBucket: "urban-computing-cb07c.appspot.com",
        messagingSenderId: "293478437107",
        appId: "1:293478437107:web:4cd5199b68f68eac542786",
        measurementId: "G-3Z5DD1SJYQ"
    };
    let myArr=[];
    const [count, setCount] =React.useState(0);

    React.useEffect(()=>{
        setTimeout(()=>{
            firebase.initializeApp(firebaseConfig);
            firebase.database().ref('/').on('value',(snap)=> {
                let obj = snap.val();
                if (obj !== null) {
                    for (var key in obj) {
                        setCount(obj[key]);
                    }
                }
            });
        },1000);
    },[]);
    React.useEffect(()=> {
            axios.get('https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
                params: {
                    key: 'AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c',
                    types: 'cafe',
                    location: '53.26,-6.15',
                    radius: '100000'
                }
            }).then(function (response) {
                const result = response.data.results;
                for (var key in result) {
                    myArr.push(result[key].geometry.location);
                }
            })
                .catch(function (error) {
                    console.log(error);
                });
        setArray(myArr);
        console.log(array);
    });
    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c"
    });

    const [map, setMap] = React.useState(null);

    const onLoad = React.useCallback(function callback(map) {
        const bounds = new window.google.maps.LatLngBounds(count);
        map.fitBounds(bounds);
        for(let i =0;i<array.length;i++)
        {
            const marker = new window.google.maps.Marker({
                position: array[i],
                title: "starting"
            });
            marker.setMap(map);
        }
    }, []);
    const onUnmount = React.useCallback(function callback(map) {
        setMap(null);
    }, []);


    return isLoaded ? (
        <div>
            <GoogleMap  mapContainerStyle={containerStyle}  center={count}  zoom={25}  onLoad={onLoad}  onUnmount={onUnmount}> </GoogleMap>
            <p style={{marginLeft:'5px',fontWeight:'bold',marginTop:'10px',color:'#0066FF'}}>搜索附近餐饮店:</p><Search style={{width:'330px',marginLeft:'110px',marginBottom:'5px',marginTop:'-42px'}} placeholder="input search text"  enterButton />
        </div>
    ) : <></>
}
