import React,{useState,useEffect}from 'react'
import { Table, Button, Space } from 'antd';
import './less/ListTable.less'
import {ArticleListApi} from "./request/api";
import {DeleteUserDataApi} from "./request/api";
//父传子
function MyTitle(props) {
    return (
        <div>
            <a className='table_title' href={"localhost:3000/delete"} target="_blank">{props.id}</a>
            {/*<p style={{ color: '#999' }}>简介:{props.subTitle}</p>*/}
        </div>
    )
}


export default function ListTable(){
    //列表数组
    const [arr,setArr]=useState([
        {   title:'',
            key:'1',
            Rating:'New York No.1 Lake Park',
        }
    ])

    const handleDelete = (key: any) => {
        DeleteUserDataApi.get('/delete', {
            params: {
                ID: key
            }
        })
            .then(function (response) {
                console.log(response);
            })
            .catch(function (error) {
                console.log(error);
            });
    };

//请求文章列表
    useEffect(()=>{
        ArticleListApi().then(res=> {
          let newArr=JSON.parse(JSON.stringify(res));//深拷贝
          let myarr=[]//用来获取自己想要的属性
          newArr.map(item=>{
              let obj= {
                  key : item.id,
                  title : <MyTitle title={item.name} />,
                  comment : item.comment,
                  score:item.score
              }
              myarr.push(obj);
          })
           setArr(myarr)
            console.log(myarr);
        })
    },[])


//每一列内容 //text.key就是id
    const columns = [
        {
            title: '文章名称',
            dataIndex: 'title',
            key: 'title',
            width:'30%',
            render: text => <div>{text}</div>
        },
        {
            title: '打分',
            dataIndex: 'score',
            key: 'score',
            render: text => <div>{text}</div>
        },
        {
            title: '评论',
            dataIndex: 'comment',
            key: 'comment',
            render: text => <div>{text}</div>
        },
        {
            title: 'Action',
            key: 'action',
            render: text => {
                return(
                <Space size="middle">
                    <Button type="primary" onClick={handleDelete.bind(text.key)}>编辑</Button>
                    <Button type="danger"  onClick={()=>console.log(text.key)}>删除</Button>
                </Space>
                )
            },
        },
    ];


    return(
        <div className='list_table' style={{ padding: '50px',height:'600px'}}>
            <Table columns={columns} dataSource={arr} />
        </div>
    )

}
