/* jshint esversion: 6 */

import React, {useEffect, useRef, useState} from 'react';
import {GoogleMap, useJsApiLoader} from '@react-google-maps/api';
import {FindUserDataApi} from './request/api';
import firebase from "firebase/compat";
import axios from "axios";
import {Card, Input} from "antd";
const { Search } = Input;



export default function GoogleMap_() {
    axios.defaults.timeout =  200000;
    axios.interceptors.request.use(function (config) {
        return config;
    }, function (error) {

        return Promise.reject(error);
    });
    axios.interceptors.response.use(function (response) {
        return response;
    }, function (error) {
        return Promise.reject(error);
    });

    const firebaseConfig = {
        apiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c",
        authDomain: "urban-computing-cb07c.firebaseapp.com",
        databaseURL: "https://urban-computing-cb07c-default-rtdb.firebaseio.com",
        projectId: "urban-computing-cb07c",
        storageBucket: "urban-computing-cb07c.appspot.com",
        messagingSenderId: "293478437107",
        appId: "1:293478437107:web:4cd5199b68f68eac542786",
        measurementId: "G-3Z5DD1SJYQ"
    };
    const [count, setCount] =React.useState({lat: 53.49332, lng: -6.3});

    React.useEffect(()=>{
            firebase.initializeApp(firebaseConfig);
            firebase.database().ref('/').on('value',(snap)=> {
                let obj = snap.val();
                if (obj !== null) {
                    for (var key in obj) {
                        if(key.split(',')[2]>'22 13:35:00') {
                            setCount(obj[key]);
                            console.log(obj[key]);
                        }
                    }
                }
            });
    },[]);

    const {isLoaded} = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c"
    });

    const [myArr, setMyArr] = React.useState([]);
    const [NameArray, setNameArray] = React.useState([]);
    var num=0;
    var search;
    function QueryData(value:string){
        search=value;
        num=num+1;
        console.log(num);
        axios.get('https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
            params: {
                key: 'AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c',
                types: value,
                location: '53.49332,-6.31718',
                radius: '1000000'
            }
        }).then(function (response) {
                const result = response.data.results;
                for (var key in result) {
                    myArr.push(result[key].geometry.location);
                    axios.get('/api/add', {
                        params: {
                            id: num,
                            search:value,
                            title: result[key].geometry.location,
                            text: result[key].name,
                            picture:result[key].photos
                        }
                    });
                }
                console.log(result[key].photos);
                console.log(num);
        })
            .catch(function (error) {
                console.log(error);
            });
    }
    var arr=[];
    function updatePosition()
    {
        arr=[];
        var mapOptions = {
            zoom: 17,
            center: {lat: 53.49332, lng: -6.31718}
        };
        console.log(count);
        let map1 = new window.google.maps.Map(document.getElementById("test"),mapOptions);
        const marker2 = new window.google.maps.Marker({
            position:count,
        });
        marker2.setMap(map1);

        FindUserDataApi({}).then(response => {
            const result = response;
            for (var key in result) {
                if (parseFloat(result[key].title.replace(/\"/g, "").split(',')[1]) !== "") {
                    var lat = [parseFloat(result[key].title.replace(/\"/g, "").split(',')[0].split(':')[1]), count.lat];
                    var lng = [parseFloat(result[key].title.replace(/\"/g, "").split(',')[1].split(':')[1].replace(/\}/g, "")), count.lng];
                    var R = 6378137;
                    var dLat = (lat[1] - lat[0]) * Math.PI / 180;
                    var dLng = (lng[1] - lng[0]) * Math.PI / 180;
                    var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat[0] * Math.PI / 180) * Math.cos(lat[1] * Math.PI / 180) * Math.sin(dLng / 2) * Math.sin(dLng / 2);
                    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
                    var d = R * c;
                    if ((d < 1000000) && (result[key].search === search)) {
                        const marker1 = new window.google.maps.Marker({
                            position: {
                                lat: parseFloat(result[key].title.replace(/\"/g, "").split(',')[0].split(':')[1]),
                                lng: parseFloat(result[key].title.replace(/\"/g, "").split(',')[1].split(':')[1].replace(/\}/g, ""))
                            },
                        });
                        marker1.setMap(map1);
                        NameArray.push([result[key].text, result[key].title]);
                        setNameArray(NameArray);
                        let myLatlng = new window.google.maps.LatLng(parseFloat(result[key].title.replace(/\"/g, "").split(',')[0].split(':')[1]), parseFloat(result[key].title.replace(/\"/g, "").split(',')[1].split(':')[1].replace(/\}/g, "")));
                        map1.setCenter(myLatlng);
                        let Try = JSON.parse(result[key].picture);
                        if (Try !== null) {
                            arr.push('https://maps.googleapis.com/maps/api/place/photo?maxwidth=300&sensor=false&key=AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c&'+result[key].picture.photoreference);
                        }
                        console.log(arr);
                    }
                }
            }
            }).catch(function (error) {
            console.log(error);
        });
    }

    const [,updateState] = React.useState();
    const forceUpdate = React.useCallback(() => updateState({}), []);

    return(
        <div>
            {/*<GoogleMap style={{Left:'10px',fontWeight:'bold',marginTop:'10px',color:'#0066FF'}}  mapContainerStyle={containerStyle}  center={count}  zoom={10}  onLoad={forceUpdate} > </GoogleMap>*/}
            <div id="test" style={{height:'600px',width:'400px'}}></div>
            <Search style={{width:'330px',marginLeft:'500px',marginTop:'-600px'}} placeholder="Search nearest store" onSearch={QueryData}></Search>
            <div style={{marginLeft:'-170px',marginTop:'-550px'}}>
                        {
                            NameArray.map(item =>(
                            <p
                                style={{
                                    width:'330px',
                                    height:'80px',
                                    marginLeft:'669px',
                                    backgroundColor:'white'
                                }}
                            >
                                <p style={{marginLeft:'10px'}}>{item[0]}</p>
                                <p style={{marginLeft:'10px'}}>{item[1]}</p>
                                {   arr.map(item=>(
                                    <img src={item} style={{marginLeft: '10px'}}/>
                                ))}
                            </p>
                        ))
                        }
            </div>
                        <Card
                            style={{
                                width: 30,
                                marginTop:530
                            }}
                            onClick={forceUpdate}
                        >
                        </Card>
            <button style={{marginLeft:'400px',marginTop:'-600px'}} onClick={updatePosition} href="#"> update </button>
            <script async defer src="https://maps.googleapis.com/maps/api/js?key=AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c&callback=initMap&language=zh-TW">
            </script>
                </div>
    );

}
