import React from 'react'
import { List,Button } from 'antd';
import {useEffect, useState} from 'react'
import {ArticleListApi} from "./request/api";
import {useNavigate} from 'react-router-dom'
function MyTitle(props) {
    return (
        <div>
            <a className='table_title' href={"https://jsonplaceholder.typicode.com/posts/"+props.id} >{props.title}</a>
            <p style={{ color: '#999' }}>简介:{props.subTitle}</p>
        </div>
    )
}
export default function Lists(){
    const navigate = useNavigate()
    const [arr,setArr]=useState([
        {
            title:'',
        }
    ])

    useEffect(()=>{
        ArticleListApi().then(res=> {
            let newArr=JSON.parse(JSON.stringify(res));//深拷贝
            let myarr=[]//用来获取自己想要的属性
            newArr.map(item=>{
                let obj= {
                    key : item.id,
                    title : <MyTitle id={item.id} title={item.title} subTitle={item.body.substr(0,10)} />,
                    userId : item.userId,
                }
                myarr.push(obj);
            })
            setArr(myarr)
            console.log(myarr);
        })
    },[])
    return(
        <div className='list_table' style={{ padding: '20px' }}>
        <List className="list_table"
            itemLayout="horizontal"
            dataSource={arr}
            renderItem={item => (
                <List.Item
                    title={<a href="jsonplaceholder.typicode.com/posts">{item.title}</a>}
                    actions={[
                        <Button type='primary' onClick={()=>navigate('/edit/'+item.id)}>编辑</Button>,
                    ]}
               />
            )}
        />
        </div>
    )

}
