import React,{useState,useEffect}from 'react'
import { Table, Button, Space } from 'antd';
import {Link} from 'react-router-dom';
import './less/ListTable.less'
import {ArticleListApi} from "./request/api";

export default function ListTable(){
    //列表数组
    const [arr,setArr]=useState([
        {   title:'',
            key:'1',
            name:'John Brown',
            userId:'New York No.1 Lake Park',
        }
    ])

//请求文章列表
    useEffect(()=>{
        ArticleListApi().then(res=> {
          console.log(res);
          let newArr=JSON.parse(JSON.stringify(res));//深拷贝
          newArr.map(item=>{
              item.key=item.id;
              item.Subtitle=item.body.CharAt(2);
          })
        })
    },[])
//每一列内容
    const columns = [
        {
            title: '文章名称',
            dataIndex: 'name',
            key: 'name',
            width:'60%',
            render: text =>(
                <>
                <Link to={"/"} className='table_title'>标题</Link>
                <p style={{color:'#999'}}>文章简介</p>
                </>
            ),
        },
        {
            title: '作者id',
            dataIndex: 'userId',
            key: 'age',
            render: text =>(
                <>
                    <h4>2022/4/19</h4>
                </>
            ),
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <Button type="primary">编辑</Button>
                    <Button type="danger">删除</Button>
                </Space>
            ),
        },
    ];


    return(
        <div className='list_table'>
            <Table columns={columns} dataSource={arr} />
        </div>
    )

}
