            }else{
                message.success(res.message)
            }
        })
    }

    useEffect(()=>{
        ArticleListApi().then(res=> {
            let newArr=JSON.parse(JSON.stringify(res));//深拷贝
            let myarr=[]//用来获取自己想要的属性
            newArr.map(item=>{
                let obj= {
                    key : item.id,
                    title : <MyTitle id={item.id} title={item.title} subTitle={item.body.substr(0,10)} />,
                    userId : item.userId,
                }
                myarr.push(obj);
            })
            setArr(myarr)
            console.log(myarr);
        })
    },[])
    return(
        <div className='list_table' style={{ padding: '20px' }}>
        <List className="list_table"
            itemLayout="horizontal"
            dataSource={arr}
            renderItem={item => (
                <List.Item
                    actions={[
                        <Button type='primary' onClick={()=>navigate('/edit/'+item.id)}>编辑</Button>,
                        <Button type='danger' onClick={()=>delFn(item.id)}>删除</Button>
                    ]}
               />
            )}
        />
        </div>
    )

}
