import React from 'react'
import { List, Avatar,Button } from 'antd';
import {useEffect, useState} from 'react'
import {ArticleListApi} from "./request/api";

function MyTitle(props) {
    return (
        <div>
            <a className='table_title' href={"https://jsonplaceholder.typicode.com/posts/"+props.id} target="_blank">{props.title}</a>
            <p style={{ color: '#999' }}>简介:{props.subTitle}</p>
        </div>
    )
}
export default function Lists(){
    const [arr,setArr]=useState([
        {
            title:'',
        }
    ])
    useEffect(()=>{
        ArticleListApi().then(res=> {
            let newArr=JSON.parse(JSON.stringify(res));//深拷贝
            let myarr=[]//用来获取自己想要的属性
            newArr.map(item=>{
                let obj= {
                    key : item.id,
                    title : <MyTitle id={item.id} title={item.title} subTitle={item.body.substr(0,10)} />,
                    userId : item.userId,
                }
                myarr.push(obj);
            })
            setArr(myarr)
            console.log(myarr);
        })
    },[])
    return(
        <div>
        <div className="list_table">
        <List
            itemLayout="horizontal"
            dataSource={arr}
            renderItem={item => (
                <List.Item>
                    <List.Item.Meta
                        avatar={<Avatar src="https://joeschmoe.io/api/v1/random" />}
                        title={<a href="https://ant.design">{item.title}</a>}
                        description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                    />
                </List.Item>
            )}
        />
        </div>
        </div>
    )

}
