import React,{useState,useEffect}from 'react'
import { Table, Button, Space } from 'antd';
import {Link} from 'react-router-dom';
import './less/ListTable.less'
import {ArticleListApi} from "./request/api";

//父传子
function MyTitle(props) {
    return (
        <div>
            <Link className='table_title' to="/" target="_blank">{props.title}</Link>
            <p style={{ color: '#999' }}>简介:{props.subTitle}</p>
        </div>
    )
}
export default function ListTable(){
    //列表数组
    const [arr,setArr]=useState([
        {   title:'',
            key:'1',
            userId:'New York No.1 Lake Park',
        }
    ])

//请求文章列表
    useEffect(()=>{
        ArticleListApi().then(res=> {
          let newArr=JSON.parse(JSON.stringify(res));//深拷贝
          let myarr=[]//用来获取自己想要的属性
          newArr.map(item=>{
              let obj= {
                  key : item.id,
                  title : <MyTitle title={item.title} subTitle={item.body.substr(0,10)} />,
                  userId : item.userId,
              }
              myarr.push(obj);
          })
           setArr(myarr)
            console.log(myarr);
        })
    },[])
//每一列内容
    const columns = [
        {
            title: '文章名称',
            dataIndex: 'title',
            key: 'title',
            width:'60%',
            render: text => <div>{text}</div>
        },
        {
            title: '作者id',
            dataIndex: 'userId',
            key: 'userId',
            render: text => <div dangerouslySetInnerHTML={{__html: text}}></div>,
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <Button type="primary">编辑</Button>
                    <Button type="danger">删除</Button>
                </Space>
            ),
        },
    ];


    return(
        <div className='list_table'>
            <Table columns={columns} dataSource={arr} />
        </div>
    )

}
