import React,{useState,useEffect}from 'react'
import { Table, Button, Space } from 'antd';
import {Link} from 'react-router-dom';
import './less/ListTable.less'
import {ArticleListApi} from "./request/api";

//父传子
function MyTitle(props) {
    return (
        <div>
            <a className='table_title' href={"http://codesohigh.com:8765/article/" + props.id} target="_blank">{props.title}</a>
            <p style={{ color: '#999' }}>{props.subTitle}</p>
        </div>
    )
}
export default function ListTable(){
    //列表数组
    const [arr,setArr]=useState([
        {   title:'',
            key:'1',
            userId:'New York No.1 Lake Park',
        }
    ])

//请求文章列表
    useEffect(()=>{
        ArticleListApi().then(res=> {
          let newArr=JSON.parse(JSON.stringify(res));//深拷贝
          newArr.map(item=>{
              item.key=item.id;
              item.title=item.title;
              item.userId=item.userId;
          })
           setArr(newArr)
            console.log(newArr);
        })
    },[])
//每一列内容
    const columns = [
        {
            title: '文章名称',
            dataIndex: 'title',
            key: 'title',
            width:'60%',
            render: text => <div dangerouslySetInnerHTML={{__html: text}} ></div>,
        },
        {
            title: '作者id',
            dataIndex: 'userId',
            key: 'userId',
            render: text => <div dangerouslySetInnerHTML={{__html: text}}></div>,
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <Button type="primary">编辑</Button>
                    <Button type="danger">删除</Button>
                </Space>
            ),
        },
    ];


    return(
        <div className='list_table'>
            <Table columns={columns} dataSource={arr} />
        </div>
    )

}
