import React,{useState,useEffect}from 'react'
import {Table, Button, Space, Modal} from 'antd';
import './less/ListTable.less'
import {ArticleListApi} from "./request/api";
import {DeleteUserDataApi} from "./request/api";
import { Input } from 'antd';
import {UserOutlined} from "@ant-design/icons";
//父传子
function MyTitle(props) {
    return (
        <div>
            <a className='table_title' href={"localhost:3000/api/findAll?id=1"} target="_blank">{props.title}</a>
            {/*<p style={{ color: '#999' }}>简介:{props.subTitle}</p>*/}
        </div>
    )
}


export default function ListTable(){
    //列表数组
    let value;
    value = ''
    const [arr,setArr]=useState([
        {   title:'',
            key:'1',
            Rating:'New York No.1 Lake Park',
        }
    ])
    const [modify,setModify]=useState([
        {   inputValue:'',
            inputScore:'1',
            inputComment:'New York No.1 Lake Park',
        }
    ])
    const [isModalVisible, setIsModalVisible] = useState(false);
    const showModal = () => {
        setIsModalVisible(true);
    };
    const handleOk = () => {
        setIsModalVisible(false);
        console.log(modify)
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };
//请求文章列表
    useEffect(()=>{
        ArticleListApi().then(res=> {
            let newArr = JSON.parse(JSON.stringify(res));//深拷贝
            let myarr = []//用来获取自己想要的属性
            newArr.map(item => {
                let obj = {
                    key: item.id,
                    title: <MyTitle title={item.name}/>,
                    comment: item.comment,
                    score: item.score
                }
                myarr.push(obj);
            })
            setArr(myarr)
            console.log(myarr)
        })
    },[])

    function DeleteUserData(arr) {
        let params={
            id:arr
        }
        DeleteUserDataApi({params}).then(function (response) {
            console.log(response);
            window.location.reload()
        })
            .catch(function (error) {
                console.log(error);
            });
    }
    useEffect(() => {
        setModify(modify);
    }, [modify])

//每一列内容 //text.key就是id
    const columns = [
        {
            title: '文章名称',
            dataIndex: 'title',
            key: 'title',
            width:'30%',
            render: text => <div>{text}</div>
        },
        {
            title: '打分',
            dataIndex: 'score',
            key: 'score',
            render: text => <div>{text}</div>
        },
        {
            title: '评论',
            dataIndex: 'comment',
            key: 'comment',
            render: text => <div>{text}</div>
        },
        {
            title: 'Action',
            key: 'action',
            render: text => {
                return(
                <Space size="middle">
                    <Button type="primary" onClick={showModal}>编辑</Button>
                    <Modal title="编辑修改" visible={isModalVisible} onOk={handleOk.bind(this,modify)} onCancel={handleCancel}>
                        <Input value={modify} placeholder="文章名称" onChange={(e) => {setModify(e.target.inputValue) }} prefix={<UserOutlined />} />
                        <br />
                        <br />
                        <Input value={modify} placeholder="打分" onChange={(e) => {setModify(e.target.inputScore) }} prefix={<UserOutlined />} />
                        <br />
                        <br />
                        <Input value={modify} placeholder="评论" onChange={(e) => {setModify(e.target.inputComment) }} prefix={<UserOutlined />} />
                        <br />
                        <br />
                    </Modal>
                    <Button type="danger"  onClick={DeleteUserData.bind(this,text.key)}>删除</Button>
                </Space>
                )
            },
        },
    ];

    return(
        <div className='list_table' style={{ padding: '50px',height:'600px'}}>
            <Table columns={columns} dataSource={arr} />
        </div>
    )

}
