/* jshint esversion: 6 */

import React, {useEffect, useRef, useState} from 'react';
import {GoogleMap, MarkerClusterer, useJsApiLoader} from '@react-google-maps/api';
import {Map, Marker, Markers} from 'react-amap';
import {FindUserDataApi, FindTextApi, ArticleSearchApi, UpdateTextApi, CommentListApi} from './request/api';
import firebase from "firebase/compat";
import axios from "axios";
import {Input} from "antd";
import ErrorBoundary from "antd/es/alert/ErrorBoundary";
const { Search } = Input;


const containerStyle = {
    width: '400px',
    height: '400px',
    float:'left'
};


export default function map() {
    const randomPosition = () => ({
        lat: -6 + Math.random() * 1,
        lng: 53 + Math.random() * 1
    });
    const randomMarker = (len) => (
        Array(len).fill(true).map((e, idx) => ({
            position: randomPosition()
        }))
    );
    const [food,setFood] = React.useState({markers: randomMarker(10)});

    const randomMarkers=function(){
        setFood({markers: randomMarker(10)});
        console.log(food);
    };

    const firebaseConfig = {
        apiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c",
        authDomain: "urban-computing-cb07c.firebaseapp.com",
        databaseURL: "https://urban-computing-cb07c-default-rtdb.firebaseio.com",
        projectId: "urban-computing-cb07c",
        storageBucket: "urban-computing-cb07c.appspot.com",
        messagingSenderId: "293478437107",
        appId: "1:293478437107:web:4cd5199b68f68eac542786",
        measurementId: "G-3Z5DD1SJYQ"
    };
    const [count, setCount] =React.useState({lat: 53.49332, lng: -6.31718});

    React.useEffect(()=>{
            firebase.initializeApp(firebaseConfig);
            firebase.database().ref('/').on('value',(snap)=> {
                let obj = snap.val();
                if (obj !== null) {
                    for (var key in obj) {
                        if(key.split(',')[2]>'22 13:35:00') {
                            setCount(obj[key]);
                            console.log(obj[key]);
                        }
                    }
                }
            });
    },[]);


    const {isLoaded} = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c"
    });
    let [map, setMap] = React.useState(null);

    const [myArr, setMyArr] = React.useState([]);

    function QueryData(value:string){
        axios.get('https://maps.googleapis.com/maps/api/place/nearbysearch/json', {
            params: {
                key: 'AIzaSyDSR-cgFYuYuXoDo4jvjxL0FbTfEN4me2c',
                types: value,
                location: '53.26,-6.15',
                radius: '100000'
            }
        }).then(function (response) {
            const result = response.data.results;
            for (var key in result) {
                myArr.push(result[key].geometry.location);
            }
            setMyArr(myArr);
            console.log(myArr);
            for(let key in myArr) {
                axios.get('/api/add', {
                    params: {
                        id:0,
                        title:myArr[key],
                        text:''
                    }
                });
            }
            window.location.reload();
        })
            .catch(function (error) {
                console.log(error);
            });
    }

    function query(){
        const marker2 = new window.google.maps.Marker({
            position: {lat: 51.49332, lng: -6.31718},
            title: "starting"
        });
        for(let key in myArr) {
            const marker = new window.google.maps.Marker({
                position: myArr[key],
                title: "starting"
            });
            marker.setMap(map);
      }
        marker2.setMap(map);
        console.log('1');
    }
    const forceUpdate= React.useCallback((map) =>{
            var mapOptions = {
                center: new window.google.maps.LatLng(53.397, -6),   //指地图定位的坐标，需要LatLng对象，对象参数就是纬度和经度
                zoom: 8,
                mapTypeId: window.google.maps.MapTypeId.ROADMAP
            };
            map = new window.google.maps.Map(document.getElementById("map"), mapOptions);

            const marker1 = new window.google.maps.Marker({
                position: count,
                title: "starting"
            });
            FindUserDataApi({}).then(response => {
                const result = response;
                for (var key in result) {
                    const marker = new window.google.maps.Marker({
                        position: {lat: parseFloat(result[key].title.replace(/\"/g, "").split(',')[0].split(':')[1]), lng: parseFloat(result[key].title.replace(/\"/g, "").split(',')[1].split(':')[1].replace(/\}/g,""))},
                    });
                    console.log(1);
                    marker.setMap(map);
                }
            });
            marker1.setMap(map);
        },[]);


    return isLoaded?(
        <div>
            <GoogleMap  id='map' mapContainerStyle={containerStyle}  center={count}  zoom={20}  onLoad={forceUpdate}> </GoogleMap>
            <p style={{marginLeft:'5px',fontWeight:'bold',marginTop:'10px',color:'#0066FF'}}>搜索附近餐饮店:</p><Search style={{width:'330px',marginLeft:'110px',marginBottom:'5px',marginTop:'-42px'}} placeholder="input search text" onSearch={QueryData}></Search>
        </div>):<></>
}
