import { DislikeFilled, DislikeOutlined, LikeFilled, LikeOutlined } from '@ant-design/icons';
import React, {createElement, useEffect, useState} from 'react';
import {Avatar, Comment, Tooltip, Button, Form, Input, List, Skeleton, Divider} from 'antd';
import moment from 'moment';
import { message } from 'antd';
import VirtualList from 'rc-virtual-list';
import {AddCommentDataApi, CommentListApi, GetCommentDataApi} from "./request/api";
import InfiniteScroll from 'react-infinite-scroll-component';


export default function Means() {
    const[arr,setArr]=useState([
        {
            actions: [<span key="comment-list-reply-to-0">Reply to</span>],
            author: 'Han Solo',
            avatar: 'https://joeschmoe.io/api/v1/random',
            comments: (
                <p>
                    We supply a series of design principles, practical patterns and high quality design
                    resources (Sketch and Axure), to help people create their product prototypes beautifully and
                    efficiently.
                </p>
            ),
            datetime: (
                <Tooltip title={moment().subtract(1, 'days').format('YYYY-MM-DD HH:mm:ss')}>
                    <span>{moment().subtract(1, 'days').fromNow()}</span>
                </Tooltip>
            ),
        },
        {
            actions: [<span key="comment-list-reply-to-0">Reply to</span>],
            author: 'Han Solo',
            avatar: 'https://joeschmoe.io/api/v1/random',
            comments: (
                <p>
                    We supply a series of design principles, practical patterns and high quality design
                    resources (Sketch and Axure), to help people create their product prototypes beautifully and
                    efficiently.
                </p>
            ),
            datetime: (
                <Tooltip title={moment().subtract(2, 'days').format('YYYY-MM-DD HH:mm:ss')}>
                    <span>{moment().subtract(2, 'days').fromNow()}</span>
                </Tooltip>
            ),
        },
    ]);


    const CommentList = ({ comments }) => (
        <List
            dataSource={comments}
            header={`${comments.length} ${comments.length > 1 ? 'replies' : 'reply'}`}
            itemLayout="horizontal"
            renderItem={(props) => <Comment {...props} />}
        />
    );

    const Editor = ({ onChange, onSubmit, submitting, value }) => (
        <>
            <Form.Item>
                <TextArea rows={4} onChange={onChange} value={value} />
            </Form.Item>
            <Form.Item>
                <Button htmlType="submit" loading={submitting} onClick={onSubmit} type="primary">
                    Add Comment
                </Button>
            </Form.Item>
        </>
    );



    useEffect(()=>{
        GetCommentDataApi().then(response=> {
            let newArr = JSON.parse(JSON.stringify(response));//Ê∑±Êã∑Ë¥ù
            let myarr = []//Áî®Êù•Ëé∑ÂèñËá™Â∑±ÊÉ≥Ë¶ÅÁöÑÂ±ûÊÄß
            newArr.map(item => {
                let obj = {
                    id: item.id,
                    avatar: 'https://joeschmoe.io/api/v1/random',
                    actions: [<span key="comment-list-reply-to-0">Reply to</span>],
                    author: item.author,
                    comments: item.comments,
                    value: item.value,
                    datetime: (
                        <Tooltip title={moment().subtract(2, 'days').format('YYYY-MM-DD HH:mm:ss')}>
                            <span>{moment().subtract(2, 'days').fromNow()}</span>
                        </Tooltip>
                    ),
                }
                myarr.push(obj);
            })
            setArr(myarr)
            console.log(myarr)
        })
    },[])

   return(
       <div  id="scrollableDiv"
             style={{
                 height: 600,
                 overflow: 'auto',
                 padding: '0 16px',
                 border: '1px solid rgba(140, 140, 140, 0.35)',
             }}
       >
           <InfiniteScroll
               dataLength={arr.length}
               hasMore={arr.length < 50}
               endMessage={<Divider plain>It is all, nothing more ü§ê</Divider>}
               scrollableTarget="scrollableDiv"
           >
       <List
           className="comment-list"
           header={`${arr.length} replies`}
           itemLayout="horizontal"
           dataSource={arr}
           renderItem={(item) => (
               <li>
                   <Comment
                       actions={item.actions}
                       author={item.author}
                       avatar={item.avatar}
                       content={item.comments}
                       datetime={item.datetime}
                   />
               </li>
           )}
       />
       </InfiniteScroll>
       </div>
    );
}
