/* jshint esversion: 6 */

import { createStore } from 'redux'

const defaultState = {
  list: []
}
const store = createStore((state = defaultState, action) => {
  console.log(action)

  switch (action.type) {
    case 'ADD_LIST': {
      return {
        ...state,
        list: action.list,
      }
    }
    default:
      break
  }
  return state
})
export default store
