import { DislikeFilled, DislikeOutlined, LikeFilled, LikeOutlined } from '@ant-design/icons';
import React, {createElement, useEffect, useState} from 'react';
import { Avatar, Comment, Tooltip, Button, Form, Input, List } from 'antd';
import moment from 'moment';
import { message } from 'antd';
import VirtualList from 'rc-virtual-list';
import {AddCommentDataApi, GetCommentDataApi} from "./request/api";
const { TextArea } = Input;


export default function Means() {
    const [likes, setLikes] = useState(0);
    const [dislikes, setDislikes] = useState(0);
    const [action, setAction] = useState(null);

    const like = () => {
        setLikes(1);
        setDislikes(0);
        setAction('liked');
    };

    const dislike = () => {
        setLikes(0);
        setDislikes(1);
        setAction('disliked');
    };

    const actions = [
        <Tooltip key="comment-basic-like" title="Like">
      <span onClick={like}>
        {createElement(action === 'liked' ? LikeFilled : LikeOutlined)}
          <span className="comment-action">{likes}</span>
      </span>
        </Tooltip>,
        <Tooltip key="comment-basic-dislike" title="Dislike">
      <span onClick={dislike}>
        {React.createElement(action === 'disliked' ? DislikeFilled : DislikeOutlined)}
          <span className="comment-action">{dislikes}</span>
      </span>
        </Tooltip>,
        <span key="comment-basic-reply-to">Reply to</span>,
    ];


    const [comments, setComments] = useState([]);
    const [submitting, setSubmitting] = useState(false);
    const [value, setValue] = useState('');
    const [author, setAuthor] = useState('');
    console.log(value)

    useEffect(()=>{
        let params={
            id:'2'
        }
        GetCommentDataApi({params}).then(res=> {
            let newArr=JSON.parse(JSON.stringify(res));//深拷贝
            newArr.map(item=>{
                    setValue(item.value)
                    setAuthor(item.author)
            })
        })
    },[])

    const CommentList = ({ comments }) => (
        <List
            dataSource={comments}
            header={`${comments.length} ${comments.length > 1 ? 'replies' : 'reply'}`}
            itemLayout="horizontal"
            renderItem={(props) => <Comment {...props} />}
        />
    );

    const Editor = ({ onChange, onSubmit, submitting, value }) => (
        <>
            <Form.Item>
                <TextArea rows={4} onChange={onChange} value={value} />
            </Form.Item>
            <Form.Item>
                <Button htmlType="submit" loading={submitting} onClick={onSubmit} type="primary">
                    Add Comment
                </Button>
            </Form.Item>
        </>
    );

    const handleSubmit = (value) => {
        let params = {
            id: '4',
            value:value,
            author:'张近东',
            comments:'niusbility'
        }
        console.log(params)
        AddCommentDataApi({params}).then(function (response) {
            console.log(response);
            window.location.reload()
        })
            .catch(function (error) {
                console.log(error);
            });
    };

    const handleChange = (e) => {
        setValue(e.target.value);
    };
    return (
        <>
            {comments.length > 0 && <CommentList comments={comments} />}
            <Comment
                avatar={<Avatar src="https://joeschmoe.io/api/v1/random" alt="Han Solo" />}
                content={
                    <Editor
                        onChange={handleChange}
                        onSubmit={handleSubmit.bind(this,value)}
                        submitting={submitting}
                        value={value}
                    />
                }
            />

            <Comment
                actions={actions}
                author={author}
                avatar={<Avatar src="https://joeschmoe.io/api/v1/random" alt="Han Solo" />}
                content={value}
                datetime={
                    <Tooltip title={moment().format('YYYY-MM-DD HH:mm:ss')}>
                        <span>{moment().fromNow()}</span>
                    </Tooltip>
                }
            />
        </>
    );
};
