import React, { useEffect, useState } from 'react'
import { PageHeader, Button, Modal, Form, Input, message } from 'antd';
import moment from 'moment'
import E from 'wangeditor'
import { ArticleAddApi, ArticleSearchApi, ArticleUpdateApi } from './request/api'
import { useParams, useNavigate, useLocation } from 'react-router-dom'

let editor = null

export default function Edit() {
    const [comment, setComment] = useState('')
    const location = useLocation()
    const [name, setName] = useState('')
    const [score, setScore] = useState('')
    const [isModalVisible, setIsModalVisible] = useState(false);
    const navigate = useNavigate()
    const [form] = Form.useForm();
    const params = useParams()

    // 处理请求数据
    const dealData = (errCode, msg) => {
        setIsModalVisible(false); // 关闭对话框
        if (errCode === 0) {
            message.success(msg)
            setTimeout(() => {
                // 跳回list页面
                navigate('/lists')
            }, 1500)
        } else {
            message.error(msg)
        }
    }

    // 对话框点击了提交
    const handleOk = () => {
        form
            .validateFields()    // validate校验   field字段
            .then((values) => {
                // form.resetFields();   // reset重置
                let { name,score,comment} = values;
                // 地址栏有id代表现在想要更新一篇文章
                if (params.id) {
                    // 更新文章的请求
                    let id;
                    id='1'
                    ArticleUpdateApi({ name, score, comment, id }).then(res => dealData(res.errCode, res.message))
                } else {
                    // 添加文章的请求
                    ArticleAddApi({ name, score, comment }).then(res => dealData(res.errCode, res.message))
                }
            })
            .catch(() => false);
    };

    // 模拟componentDidMount
    useEffect(() => {
        editor = new E('#div1')
        editor.config.onchange = (newHtml) => {
            setComment(newHtml)
        }
        editor.create()

        // 根据地址栏id做请求
        if (params.id) {
            ArticleSearchApi({ id: '6'}).then(res => {
                if (res.errCode === 0) {
                    editor.txt.html(res.data.comment) // 重新设置编辑器内容
                    setName(res.data.name)
                    setScore(res.data.score)
                }
            })
        }

        return () => {
            // 组件销毁时销毁编辑器  注：class写法需要在componentWillUnmount中调用
            editor.destroy()
        }
    }, [location.pathname])
    function handleFinish(){

    }
    return (
        <div>
            <PageHeader
                ghost={false}
                onBack={params.id ? () => window.history.back() : null}
                title="文章编辑"
                subTitle={"当前日期：" + moment(new Date()).format("YYYY-MM-DD")}
                extra={<Button key="1" type="primary" onClick={() => setIsModalVisible(true)}>提交文章</Button>}
            >
            </PageHeader>

            <div id="div1" style={{ padding: '0 20px 20px', background: '#fff' }}></div>
            <Modal zIndex={99999} title="填写文章标题" visible={isModalVisible} onOk={handleOk} onCancel={() => setIsModalVisible(false)} okText="提交" cancelText="取消">
                <Form
                    form={form}
                    name="basic"
                    labelCol={{ span: 3 }}
                    wrapperCol={{ span: 21 }}
                    autoComplete="off"
                    initialValues={{ name, score }}
                >
                    <Form.Item
                        label="标题"
                        name="title"
                        rules={[{ required: true, message: '请填写标题' }]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        label="副标题"
                        name="score"
                    >
                        <Input />
                    </Form.Item>
                </Form>
            </Modal>
        </div>
    )
}
