import { DislikeFilled, DislikeOutlined, LikeFilled, LikeOutlined } from '@ant-design/icons';
import React, {createElement, useEffect, useState} from 'react';
import { Avatar, Comment, Tooltip, Button, Form, Input, List } from 'antd';
import moment from 'moment';
import { message } from 'antd';
import VirtualList from 'rc-virtual-list';
import {AddCommentDataApi, CommentListApi, GetCommentDataApi} from "./request/api";
const { TextArea } = Input;


export default function Means() {
    const[arr,setArr]=useState([{id:'',author:'',comments:'',value:''}])
    const data = [
        {
            title: 'Ant Design Title 1',
        },
        {
            title: 'Ant Design Title 2',
        },
        {
            title: 'Ant Design Title 3',
        },
        {
            title: 'Ant Design Title 4',
        },
    ];

    useEffect(()=>{
        GetCommentDataApi().then(response=> {
            let newArr = JSON.parse(JSON.stringify(response));//深拷贝
            let myarr = []//用来获取自己想要的属性
            newArr.map(item => {
                let obj = {
                    id: item.id,
                    author: item.author,
                    comments: item.comments,
                    value: item.value
                }
                myarr.push(obj);
            })
            setArr(myarr)
            console.log(myarr)
        })
    },[])

   return(
        <List
            itemLayout="horizontal"
            dataSource={arr}
            renderItem={(item) => (
                <List.Item>
                    <List.Item.Meta
                        avatar={<Avatar src="https://joeschmoe.io/api/v1/random" />}
                        title={<a href="https://ant.design">{item.title}</a>}
                        description={item.value}
                    />
                </List.Item>
            )}
        />
    );
}
